        var currentDate = new Date();
        if ((currentDate.getMonth() + 1) <= 3) {
          startYear = currentDate.getFullYear() - 1;
          endYear = currentDate.getFullYear();
        } else {
          startYear = currentDate.getFullYear();
          endYear = currentDate.getFullYear() + 1;
