                            // let builderResult = this._componentBuilderServiceBase.CreateComponet(widgetItem.widget, placeHolder)

                            // if (!builderResult.isSuccess) { return; }
                           
                            // if(environment.debugWidget){
                            //     builderResult.componentRef.location.nativeElement.setAttribute("widget_page_id",  widgetItem.id);
                            //     builderResult.componentRef.location.nativeElement.setAttribute("page_name",  this.pageReq.page);
                            //     builderResult.componentRef.location.nativeElement.setAttribute("widget_name", widgetItem.widget)
                            // }

                            // this.compRefInstancesMap.set(widgetItem.position, builderResult.componentRef.instance)

                            let properties = JSON.parse(widgetItem.properties);

                            // builderResult.componentRef.instance.dataModel = this.ObjectDeepAssign(builderResult.componentRef.instance.dataModel, properties);
                            // if(builderResult.componentRef.instance.dataModel){
                                // builderResult.componentRef.instance.dataModel.PAGE_NAME = this.pageReq.page;
                            // }
                           
                        
                            const submitData = this.submitData.filter(item => item.position == widgetItem.position);
                            let submitDataItem = submitData[0];


                            // widget submit data properties mapping
                            // if (builderResult.componentRef.instance.dataModel.submitProperties && submitDataItem) {
                            //     // if (submitData.length == 0) {
                            //     //     console.log(widgetItem.position)
                            //     //     return;
                            //     // }
                            //     const submitProperties = JSON.parse(submitDataItem.properties);
                            //     const dataPositions = JSON.parse(submitDataItem.datapositions);
                            //     builderResult.componentRef.instance.dataModel.submitProperties = submitProperties;
                            //     Object.assign(builderResult.componentRef.instance.dataModel.submitProperties, dataPositions);
                            //     builderResult.componentRef.instance.dataModel.submitProperties.id = submitDataItem.id;
                            //     builderResult.componentRef.instance.dataModel.submitProperties.submitcode = submitDataItem.submitcode;
                            // }

                            // builderResult.componentRef.instance.dataModel.widgetinstanceid = widgetItem.id

                            // builderResult.componentRef.instance.dataModel.globalParameters = this.globalParameters;

                            if (widgetItem.eventactions) {
                                let eventActionsMap = new Map(JSON.parse(widgetItem.eventactions))
                                // builderResult.componentRef.instance.configModel.EventAction = eventActionsMap;
                            }

                            // builderResult.componentRef.instance.configModel.EventAction.forEach((events: any, eventName: string) => {
                                // builderResult.componentRef.instance.configModel.CompToCaller.addListener(eventName, (eventparams) => {
                                    // events.forEach(eventItem => {
                                    //     this.componentEventHandler(builderResult.componentRef.instance.dataModel,eventItem, eventparams)
                                    // });
                                // }
                                // );
                            // });
                            // builderResult.componentRef.instance.configModel.CompToCaller.addListener(WidgetConstants.ON_REQUEST, (eventparams) => {
                                    // this.pageEventHandler(WidgetConstants.ON_REQUEST, eventparams)
                            // });

                            // builderResult.componentRef.instance.configModel.CompToCaller.addListener(WidgetConstants.ON_REQUEST_COMPLETED, (eventparams) => {
                                // this.pageEventHandler(WidgetConstants.ON_REQUEST_COMPLETED, eventparams)
                            // });

                            // if (builderResult.componentRef.instance.InitComponent) {
                            //     builderResult.componentRef.instance.InitComponent();
                            // }                            
                        }

                    } catch (e) {
                        console.error(e);
                        console.log("Error widget:", widgetItem)
                    }

                });

                // console.log("compRefInstancesMap", this.compRefInstancesMap);
            }

        }, error => {
            switch (error.code) {
                case ErrorCodes.INVALID_USER_ACCESS:
                    //this._notificationService.error('Invalid User Access');
                    break;
            }
        });
    }

    ObjectDeepAssign(target, source) {
        let output = Object.assign({}, target);
        Object.keys(source).forEach(key => {
            if (Object.prototype.toString.call(source[key]) == '[object Object]') {
                if (target[key]) {
                    const obj = this.ObjectDeepAssign(target[key], source[key])
                    Object.assign(output, { [key]: obj });
                } else {
                    Object.assign(output, { [key]: source[key] });
                }

            } else {
                Object.assign(output, { [key]: source[key] });
            }
        })
        return output;
    }

    componentEventHandler(wigDataContext: any,eventItem: any, eventparams: any) {
        this.eventHandler = this._eventActionServiceBase.getEventHandler(eventItem.action)
        this.eventHandler(wigDataContext, eventItem.params, eventparams, eventItem.posteventaction) //Executing method in current context
    }

    pageEventHandler(eventItem: any, eventparams: any) {
        switch(eventItem){
            case WidgetConstants.ON_REQUEST:
              
                 this.showSpinner()
                break;
            case WidgetConstants.ON_REQUEST_COMPLETED:
               
              
                    this.hideSpinner()
                            
                break;
        }
    }

    showSpinner(){
        if (this._baseSpinner) {
            this.requestCount++;
            this._baseSpinner.show()  
        }        
     }
   
     hideSpinner(){
        if(this._baseSpinner){
            this.requestCount--;
            if(this.requestCount == 0){
                this._baseSpinner.hide()
            }
        }  
     }

    RefreshData() {
        this.compRefInstancesMap.forEach(instance => {
            instance.configModel.CallerToComp.emit(WidgetConstants.CALLER_TO_COMP_REFRESH_DATA);
        });

    }

    removeListeners() {
        this.compRefInstancesMap.forEach(instance => {
            instance.configModel.CompToCaller.removeAllListeners();
        });
    }

    OnPropChangedRemoveListeners() {
        this.compRefInstancesMap.forEach(instance => {
            instance.configModel.CompToCaller.removeAllListeners();
        });
    }

    async onSelectedWFPropChange() {
        await this.OnPropChangedRemoveListeners();
        await this.clearPlaceHolderRef();
        this.getPageData();
    }

    clearPlaceHolderRef() {
        // this.placeHolders.forEach((pRef) => {
        //     let viewContainerRef = pRef.viewContainerRef;
        //     viewContainerRef.clear();
        // })
    }

}

import { FormModeConstant, WidgetConstants } from './widget-constants';
import { FormGroup, FormBuilder, NgForm } from '@angular/forms';
// import { ServerApiInterfaceServiceService } from 'src/app/server-api-interface-service.service';
import { DatePipe } from '@angular/common';
import { ValidationService } from 'src/app/services/common/validation.service';
import { WidgetHelper } from './widget-helper';
import { ElementRef, ViewChild, Directive } from '@angular/core';
import { ServerApiInterfaceServiceService } from 'src/app/services/common/server-api-interface-service.service';
declare var $: any;

@Directive()
export abstract class WidgetComponentBase {
  wgFormGroup?: FormGroup;
  dataModel: any;
  configModel: any;
  isSubmitted?: boolean;
  wgFormData: any;

  id?: string;
  apiDataUrl?: string;
  globalParameters?: Map<string, any>;
  fieldApiCount?: number;
  @ViewChild('formComponent') formComponent?: NgForm;
  tempPermission:any = true;

  abstract ConvertData(response: any):any;
  abstract setFieldData():any;
  abstract setMode(responseDataModel: any):any;
  abstract SetValue(responseDataModel: any):any;


  constructor(
    private _formBuilderBase: FormBuilder,
    private _serverApiBase: ServerApiInterfaceServiceService,
    private _datepipeBase: DatePipe,
    public _validationServiceBase: ValidationService) {
  }

  wgOnInint() {
    this.configModel.CallerToComp.addListener(WidgetConstants.CALLER_TO_COMP_REFRESH_DATA, () => {
      this.wgRefreshData();
    });

    this.wgSetRequestData();

    this.wgPrepareFieldData().then(() => {
      this.wgCreateForm();
      if (this.dataModel.isSelfDataLoad) {
        this.wgRefreshData();
      } else {      
        this.wgGetPermissionData().then((response: any) => {
          if(response){
            this.dataModel.permission = response.permission;
            this.tempPermission = response.permission;
          }  
        })

      }
    });

    this.wgSetFormData();
  }

  wgInitComponent() {

  }

  wgRefreshData() {
    this.wgSetRequestData();
    this.wgSetFormData();

    this.wgGetControlData().then((response: any) => {
        let responseDataModel = this.ConvertData(response)
        this.setMode(responseDataModel);
        this.wgSetModeFromGlobalParameter();
        // this.wgCreateForm(responseDataModel);
        if (this.dataModel.mode === FormModeConstant.EDIT || this.dataModel.mode === FormModeConstant.VIEW) {
          this.SetValue(responseDataModel);
        }        
    }).then((previousResult: any) => {
      this.wgGetPermissionData().then((response: any) => {
        if(response){
          this.dataModel.permission = response.permission;
          this.tempPermission = response.permission;
        }        
      })
    }).then((previousResult: any) => {

    });
  }

  wgCreateForm(responseDataModel?: any) {
    if (this._validationServiceBase) {
      this.wgFormGroup = this._validationServiceBase.createFormGroup(this.dataModel.validators, this.dataModel.permission, this.dataModel.mode);
    }
  }

  Validate(): boolean {
    this.isSubmitted = true;
    if (this.formComponent) {
      this.formComponent.onSubmit(null!);
    }
    return this.wgFormGroup!.valid;
  }

  onSubmit($event: Event): boolean {
    return true;
  }

  wgSetModeFromGlobalParameter() {
    if(this.dataModel.globalParameters.get("mode")){
      this.dataModel.mode = this.dataModel.globalParameters.get("mode");
    }
  }

  wgSetRequestData() {
    if (this.dataModel.isGlobalParams) {
      this.dataModel.globalParamterKeys.forEach((item:any) => {
        this.dataModel.apireqdata[item] = this.dataModel.globalParameters.get(item) ? this.dataModel.globalParameters.get(item) : null;
      })
    }
  }

  wgSetFormData() {

    if (this.dataModel.isGlobalParams && this.wgFormData) {
      this.dataModel.globalParamterKeys.forEach((item:any) => {
        this.wgFormData[item] = this.dataModel.globalParameters.get(item);
      })
    }
  }
  
  wgGetControlData() {
    this.wgOnRequest();
    return new Promise((resolve, reject) => {
      const apiUrlConst = this.dataModel.apiDataUrl;
      const reqData = Object();
      reqData.data = Object();
      reqData.data = this.dataModel.apireqdata;
      reqData.data.id = this.dataModel.widgetinstanceid;

      this._serverApiBase.post<any, any>(apiUrlConst, reqData.data).subscribe(
        response => {
          this.wgOnRequestCompleted();
          try {
            if (response) {
              resolve(response)
            }
          } catch (e) {
          }
        }, error => {
          this.wgOnRequestCompleted();
          reject()
        }
      );
    })
  }

  wgGetPermissionData() {
    if (!this.dataModel.isRequiredPermissionConfig) {
      return new Promise((resolve, reject) => {
        resolve(null);
      });
    }
    this.wgOnRequest();
    return new Promise((resolve, reject) => {
      const apiUrlConst = this.dataModel.apiPermissionConfigUrl;
      const reqData = Object();
      reqData.data = Object();
      reqData.data = this.dataModel.apireqdata;
      reqData.data.id = this.dataModel.widgetinstanceid;

      this._serverApiBase.post<any, any>(apiUrlConst, reqData.data).subscribe(
        response => {
          this.wgOnRequestCompleted();
          try {
            if (response) {
              resolve(response)
            }
          } catch (e) {
          }
        }, error => {
          this.wgOnRequestCompleted();
          reject()
        }
      );
    })
  }

  wgPrepareFieldData() {
    let isResolved = this.setFieldData();
    return Promise.all([isResolved])
  }

  wgGetReadPermission(controlName: string) {
    let mode = this.dataModel.globalParameters.get("mode") ?  this.dataModel.globalParameters.get("mode") : FormModeConstant.VIEW;

    return WidgetHelper.getReadPermissions(controlName, this.dataModel.permission, mode, this.wgFormGroup);
  }

  wgGetVisiblePermission(controlName: string) {
    //$('[data-toggle="popover"]').popover({ placement: "right" })
    let mode = this.dataModel.globalParameters.get("mode") ?  this.dataModel.globalParameters.get("mode") : FormModeConstant.VIEW;
    return WidgetHelper.getVisiblePermissions(controlName, this.dataModel.permission, mode);
  }

  wgGetLabelShortHelpText(name: string) {
    return WidgetHelper.getLabelShortHelpText(this.dataModel, name);
  }

  wgGetIconDetailsHelpText(name: string) {
    return WidgetHelper.getIconDetailsHelpText(this.dataModel, name);
  }

  wgHasHelpIcon(name: string) {
    return WidgetHelper.hasHelpIcon(this.dataModel, name);
  }

  get wgFC() { return this.wgFormGroup!.controls; }

  get fg() { return this.wgFormGroup; }

  wgOnRequest() {
    this.configModel.CompToCaller.emit(WidgetConstants.ON_REQUEST, null);
  }

  wgOnRequestCompleted() {
    this.configModel.CompToCaller.emit(WidgetConstants.ON_REQUEST_COMPLETED, null);
  }

  wgDisableControl() {
    Object.keys(this.wgFC).forEach(key => {
      this.wgFC[key].disable();
    });
  }

  wgEnabledControl() {
    Object.keys(this.wgFC).forEach(key => {
      this.wgFC[key].enable();
    });
  }
  
  wgReset(){
    if (this.formComponent) {
      this.formComponent.resetForm();
    }
    this.isSubmitted = false;
    this.wgFormGroup!.reset();
  }

}

